update "presentazione_istanza"."template_email"
set "testo" = '<p>Si comunica che, con la presente PEC, l’Ente Delegato {ENTE_PROCEDENTE} ha effettuato la trasmissione della pratica <strong>{CODICE_FASCICOLO}</strong></p><p>&nbsp;</p><ul><li>tipologia di procedura: {TIPO_PROCEDIMENTO}</li><li>richiedente: {NOME_COGNOME_RICHIEDENTE}</li><li>oggetto dell’intervento: {OGGETTO}</li><li>comune/i di intervento: {COMUNE}</li><li>data rilascio: {DATA_PROTOCOLLO_ISTANZA}</li></ul><p><br></p><p><a href="{URL_DOWNLOAD_RICEVUTA_PROTETTO}" rel="noopener noreferrer" target="_blank">Clicca qui</a> per scaricare il documento di trasmissione.</p><p>Cordiali saluti.</p><p class="ql-align-center">Si prega di non rispondere a questa e-mail in quanto la stessa è prodotta in automatico dal sistema SIT Puglia (www.sit.puglia.it).</p><p class="ql-align-center"><br></p>'
where "codice" = 'INVIO_TRASMISSIONE';