INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 27, 'checkbox'   , 'i', 4    , '1. Incremento di volume non superiore al 10 per cento della volumetria della costruzione originaria e comunque non superiore a 100mc;', 101);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 28, 'checkbox'   , 'i', 4    , '2. Interventi di demolizione e ricostruzione con il rispetto di volumetria e sagomature preesistenti;', 102);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 29, 'checkbox'   , 'i', 4    , '3. Interventi di demolizione senza ricostruzione o demolizioni di superfetazioni;', 103);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 30, 'checkbox'   , 'i', 4    , '4. Interventi sui prospetti degli edifici esistenti, quali:\n - aperture di porte e finestre o modifica delle aperture esistenti per dimensione e posizione; - interventi sulle finiture esterne, con rifacimento di intonaci, tinteggiature o rivestimenti esterni, - modificativi di quelli preesistenti; - realizzazione o modifica di balconi o terrazze; - inserimento o modifica di cornicioni, ringhiere, parapetti; chiusura di terrazze o di balconi già chiusi su tre lati mediante installazione di infissi; - realizzazione, modifica o sostituzione di scale esterne\n (la presente voce non si applica agli immobili soggetti a tutela ai sensi dell''articolo 136, comma 1, lettere a), b) e c), del Codice);', 104);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 31, 'checkbox'   , 'i', 4    , '5. interventi sulle coperture degli edifici esistenti, quali:\n - rifacimento del manto del tetto e delle lattonerie con materiale diverso; - modifiche indispensabili per l''installazione di impianti tecnologici; - modifiche alla inclinazione o alla configurazione delle falde; - realizzazione di lastrici solari o terrazze a tasca di piccole dimensioni; - inserimento di canne fumarie o comignoli; - realizzazione o modifica di finestre a tetto e lucernari; - realizzazione di abbaini o elementi consimili\n (la presente voce non si applica agli immobili soggetti a tutela ai sensi dell''articolo 136, comma 1, lettere a), b) e c), del Codice);', 105);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 32, 'checkbox'   , 'i', 4    , '6. modifiche che si rendono necessarie per l''adeguamento alla normativa antisismica ovvero per il contenimento dei consumi energetici degli edifici;', 106);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 33, 'checkbox'   , 'i', 4    , '7. realizzazione o modifica di autorimesse pertinenziali, collocate fuori terra ovvero parzialmente o totalmente interrate, con volume non superiore a 50 mc, compresi percorsi di accesso ed eventuali rampe. Ogni successivo intervento di realizzazione o modifica di autorimesse pertinenziale allo stesso immobile è sottoposto a procedura autorizzatoria ordinaria;', 107);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 34, 'checkbox'   , 'i', 4    , '8. realizzazione di tettoie, porticati, chioschi da giardino e manufatti consimili aperti su più lati, aventi una superficie non superiore a 30 mq;', 108);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 35, 'checkbox'   , 'i', 4    , '9. realizzazione di manufatti accessori o volumi tecnici di piccole dimensioni (volume non superiore a 10 mc);', 109);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 36, 'checkbox'   , 'i', 4    , '10. interventi necessari al superamento delle barriere architettoniche, anche comportanti modifica dei prospetti o delle pertinenze esterne degli edifici, ovvero realizzazione o modifica di volumi tecnici. Sono fatte salve le procedure semplificate ai sensi delle leggi speciali di settore(la presente voce non si applica agli immobili soggetti a tutela ai sensi dell''articolo 136, comma 1, lettere a), b) e c), del Codice);', 110);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 37, 'checkbox'   , 'i', 4    , '11. realizzazione o modifica di cancelli, recinzioni, o muri di contenimento del terreno (la presente voce non si applica agli immobili soggetti a tutela ai sensi dell''articolo 136, comma 1, lettere a), b) e c), del Codice);(la presente voce non si applica agli immobili soggetti a tutela ai sensi dell''articolo 136, comma 1, lettere a), b) e c), del Codice);', 111);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 38, 'checkbox'   , 'i', 4    , '12. interventi di modifica di muri di cinta esistenti senza incrementi di altezza;', 112);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 39, 'checkbox'   , 'i', 4    , '13. interventi sistematici nelle aree di pertinenza di edifici esistenti, quali: pavimentazioni, accessi pedonali e carrabili di larghezza non superiore a 4 m, modellazioni del suolo, rampe o arredi fissi (la presente voce non si applica agli immobili soggetti a tutela ai sensi dell''articolo 136, comma 1, lettere a), b) e c), del Codice);', 113);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 40, 'checkbox'   , 'i', 4    , '14. realizzazione di monumenti ed edicole funerarie all''interno delle zone cimiteriali;', 114);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 41, 'checkbox'   , 'i', 4    , '15. posa in opera di cartelli e altri mezzi pubblicitari non temporanei di cui all''art. 153, comma 1 del Codice, di dimensioni inferiori a 18 mq, ivi comprese le insegne per le attività commerciali o pubblici esercizi(la presente voce non si applica agli immobili soggetti a tutela ai sensi dell''articolo 136, comma 1, lettere a), b) e c), del Codice);', 115);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 42, 'checkbox'   , 'i', 4    , '16. collocazione di tende da sole sulle facciate degli edifici per locali destinati ad attività commerciali e pubblici esercizi;', 116);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 43, 'checkbox'   , 'i', 4    , '17. interventi puntuali di adeguamento della viabilità esistente, quali: adeguamento di rotatorie, riconfigurazione di incroci stradali, realizzazione di banchine e marciapiedi, manufatti necessari per la sicurezza della circolazione, nonché quelli relativi alla realizzazione di parcheggi a raso a condizione che assicurino la permeabilità del suolo, sistemazione e arredo di aree verdi;', 117);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 44, 'checkbox'   , 'i', 4    , '18. interventi di allaccio alle infrastrutture a rete, ove comportanti la realizzazione di opere in soprasuolo;', 118);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 45, 'checkbox'   , 'i', 4    , '19. linee elettriche e telefoniche su palo a servizio di singole utenze di altezza non superiore, rispettivamente, a metri 10 e a metri 6,30;', 119);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 46, 'checkbox'   , 'i', 4    , '20. adeguamento di cabine elettriche o del gas, ovvero sostituzione delle medesime con altre di tipologia e dimensioni analoghe;', 120);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 47, 'checkbox'   , 'i', 4    , '21. interventi sistematici di arredo urbano comportanti l''installazione di manufatti e componenti, compresi gli impianti di pubblica illuminazione;', 121);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 48, 'checkbox'   , 'i', 4    , '22. installazione di impianti tecnologici esterni per uso domestico autonomo, quali condizionatori e impianti di climatizzazione dotati di unità esterna, caldaie, parabole, antenne(la presente voce non si applica agli immobili soggetti a tutela ai sensi dell''articolo 136, comma 1, lettere a), b) e c), del Codice);', 122);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 49, 'checkbox'   , 'i', 4    , '23. parabole satellitari condominiali e impianti di condizionamento esterni centralizzati, nonché impianti per l''accesso alle reti di comunicazione elettronica di piccole dimensioni con superficie non superiore ad 1 mq o volume non superiore ad 1 mc(la presente voce non si applica agli immobili soggetti a tutela ai sensi dell''articolo 136, comma 1, lettere a), b) e c), del Codice);', 123);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 50, 'checkbox'   , 'i', 4    , '24. Installazione di impianti di radiocomunicazioni elettroniche mobili, di cui all''articolo 87 del decreto legislativo 1° agosto 2003, n. 259, che comportino la realizzazione di supporti di antenne non superiori a 6 metri se collocati su edifici esistenti, e/o la realizzazione di sopralzi di infrastrutture esistenti come pali o tralicci, non superiori a 6 metri, e/o la realizzazione di apparati di telecomunicazioni a servizio delle antenne, costituenti volumi tecnici, tali comunque da non superare l''altezza di metri 3 se collocati su edifici esistenti e di metri 4 se posati direttamente a terra;', 124);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 51, 'checkbox'   , 'i', 4    , '25. installazione in soprasuolo di serbatoi di GPL di dimensione non superiore a 13 mc, e opere di recinzione e sistemazione correlate;', 125);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 52, 'checkbox'   , 'i', 4    , '26. impianti tecnici esterni al servizio di edifici esistenti a destinazione produttiva, quali sistemi per la canalizzazione dei fluidi mediante tubazioni esterne, lo stoccaggio dei prodotti e canne fumarie;', 126);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 53, 'checkbox'   , 'i', 4    , '27. posa in opera di manufatti completamente interrati (serbatoi, cisterne etc.), che comportino la modifica della morfologia del terreno, comprese opere di recinzione o sistemazione correlate;', 127);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 54, 'checkbox'   , 'i', 4    , '28. pannelli solari, termici e fotovoltaici fino ad una superficie di 25 mq (la presente voce non si applica nelle zone territoriali omogenee \"A\" di cui all''articolo 2 del decreto ministeriale n. 1444 del 1968, e ad esse assimilabili, e nelle aree vincolate ai sensi dell''articolo 136, comma 1, lettere b) e c), del Codice), ferme restando le diverse e più favorevoli previsioni del decreto legislativo 30 maggio 2008, n. 115, recante \"Attuazione della direttiva 2006/32/CE relativa all''efficienza degli usi finali dell''energia e i servizi energetici e abrogazione della direttiva 93/76/CEE\", e dell''articolo 1, comma 289, della legge 24 dicembre 2007, n. 244, recante \"Disposizioni per la formazione del bilancio annuale e pluriennale dello Stato(legge finanziaria 2008)\";', 128);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 55, 'checkbox'   , 'i', 4    , '29. nuovi pozzi, opere di presa e prelievo da falda per uso domestico, preventivamente assentiti dalle Amministrazioni competenti, comportanti la realizzazione di manufatti in soprasuolo;', 129);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 56, 'checkbox'   , 'i', 4    , '30. tombinamento parziale di corsi d''acqua per tratti fino a 4 m ed esclusivamente per dare accesso ad abitazioni esistenti e/o a fondi agricoli interclusi, nonché la riapertura di tratti tombinati di corsi d''acqua;', 130);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 57, 'checkbox'   , 'i', 4    , '31. interventi di ripascimento localizzato di tratti di arenile in erosione, manutenzione di dune artificiali in funzione antierosiva, ripristino di opere di difesa esistenti sulla costa;', 131);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 58, 'checkbox'   , 'i', 4    , '32. ripristino e adeguamento funzionale di manufatti di difesa dalle acque delle sponde dei corsi d''acqua e dei laghi;', 132);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 59, 'checkbox'   , 'i', 4    , '33. taglio selettivo di vegetazione ripariale presente sulle sponde o sulle isole fluviali;', 133);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 60, 'checkbox'   , 'i', 4    , '34. riduzione di superfici boscate in aree di pertinenza di immobili esistenti, per superfici non superiori a 100 mq, preventivamente assentita dalle amministrazioni competenti;', 134);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 61, 'checkbox'   , 'i', 4    , '35. ripristino di prati stabili, prati pascolo, coltivazioni agrarie tipiche, mediante riduzione di aree boscate di recente formazione per superfici non superiori a 5000 mq, preventivamente assentiti dalle amministrazioni competenti;', 135);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 62, 'checkbox'   , 'i', 4    , '36. taglio di alberi isolati o in gruppi, ove ricompresi nelle aree di cui all''articolo 136, comma 1, lettere c) e d), del Codice, preventivamente assentito dalle amministrazioni competenti;', 136);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 63, 'checkbox'   , 'i', 4    , '37. manufatti realizzati in legno per ricovero attrezzi agricoli, con superficie non superiore a 10 mq;', 137);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 64, 'checkbox'   , 'i', 4    , '38. occupazione temporanea di suolo privato, pubblico, o di uso pubblico, con strutture mobili, chioschi e simili, per un periodo superiore a 120 giorni;', 138);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 65, 'checkbox'   , 'i', 4    , '39. strutture stagionali non permanenti collegate ad attività turistiche, sportive o del tempo libero, da considerare come attrezzature amovibili.', 139);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 66, 'checkbox'   , 'e', 4    , 'Istanza di rinnovo di autorizzazione paesaggistica anche rilasciata ai sensi dell''art 146 del codice scaduta da non più di un anno e relative ad interventi in tutto o in parte non eseguiti, per progetti conformi a quanto in precedenza autorizzato e alle specifiche prescrizioni di tutela eventualmente sopravvenute.', 1);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 67, 'checkbox'   , 'e', 400  , 'Le opere rientrano tra gli interventi di lieve entità di cui all''allegato B al d.P.R. 31/2017:', 2);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 68, 'checkbox'   , 'j', 40000, 'B.1. incrementi di volume non superiori al 10 per cento della volumetria della costruzione originaria e comunque non superiore a 100 mc, eseguiti nel rispetto delle caratteristiche architettoniche, morfotipologiche, dei materiali e delle finiture esistenti. Ogni ulteriore incremento sullo stesso immobile da eseguirsi nei 5 anni successivi all''ultimazione lavori è sottoposto a procedimento autorizzatorio ordinario.', 201);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 69, 'checkbox'   , 'j', 40000, 'B.2. realizzazione o modifica di aperture esterne o finestre a tetto riguardanti beni vincolati ai sensi del Codice, art. 136, comma 1, lettere a), b) e C) limitatamente, per quest''ultima, agli immobili di interesse storico-architettonico o storico-testimoniale, ivi compresa l''edilizia rurale tradizionale, isolati o ricompresi nei centri o nuclei storici, purchè tali interventi siano eseguiti nel rispetto delle caratteristiche architettoniche, morfo-tiologiche, dei materiali e delle finiture esistenti.', 202);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 70, 'checkbox'   , 'j', 40000, 'B.3. interventi sui prospetti, diversi da quelli di cui alla voce B.2., comportanti alterazione dell''aspetto esteriore degli edifici mediante modifica delle caratteristiche architettoniche, morfotipologiche, dei materiali o delle finiture esistenti, quali modifica delle facciate mediante realizzazione o riconfigurazione di aperture esterne, ivi comprese vetrine e dispositivi di protezione delle attività economiche, o di manufatti quali cornicioni, ringhiere, parapetti, interventi sulle finiture esterne, con rifacimento di intonaci, tinteggiature o rivestimenti esterni, modificativi di quelli preesistenti; realizzazione, modifica o chiusura di balconi o terrazze; realizzazione o modifica sostanziale di scale esterne.', 203);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 71, 'checkbox'   , 'j', 40000, 'B.4. interventi sulle coperture, diversi da quelli di cui alla voce B.2., comportanti alterazione dell''aspetto esteriore degli edifici mediante modifica delle caratteristiche architettoniche, morfotipologiche, dei materiali o delle finiture esistenti, quali rifacimento del manto del tetto con materiali diversi; modifiche alle coperture finalizzate all''installazione di impianti tecnologici; modifiche alla inclinazione o alla configurazione delle falde; realizzazione di lastrici o terrazze a tasca; inserimento di canne fumarie o comignoli; realizzazione di finestre a tetto, lucernari, abbaini o elementi consimili.', 204);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 72, 'checkbox'   , 'j', 40000, 'B.5. Interventi di adeguamento alla normativa antisismica ovvero finalizzati al contenimento dei consumi energetici degli edifici, laddove comportanti innovazione nelle caratteristiche morfotipologiche, ovvero nei materiali di finitura o di rivestimento preesistenti.', 205);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 73, 'checkbox'   , 'j', 40000, 'B.6. interventi necessari per il superamento di barriere architettoniche, laddove comportanti la realizzazione di rampe per il superamento di dislivelli superiori a 60 cm, ovvero la realizzazione di ascensori esterni o di manufatti consimili che alterino la sagoma dell''edificio e siano visibili dallo spazio pubblico.', 206);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 74, 'checkbox'   , 'j', 40000, 'B.7. installazione di impianti tecnologici esterni a servizio di singoli edifici, quali condizionatori e impianti di climatizzazione dotati di unità esterna, caldaie, parabole, antenne su prospetti prospicenti la pubblica via o in posizioni comunque visibili dallo spazio pubblico, o laddove si tratti di impianti non integrati nella configurazione esterna degli edifici oppure qualora tali installazioni riguardino beni vincolati ai sensi del Codice, art. 136, comma 1, lettera a), b) e c) limitatamente, per quest''ultima, agli immobili di interesse storico-architettonico o storico-testimoniale, ivi compresa l''edilizia rurale tradizionale, isolati o ricompresi nei centri o nuclei storici.', 207);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 75, 'checkbox'   , 'j', 40000, 'B.8. installazione di pannelli solari (termici o fotovoltaici) a servizio di singoli edifici, purchè integrati nella configurazione delle coperture, o posti in aderenza ai tetti degli edifici con la stessa inclinazione e lo stesso orientamento della falda degli edifici ricadenti fra quelli di cui art. 136, comma 1, lettera a), b) e c), del decreto legislativo 22 gennaio 2004, n. 42; installazione di pannelli solari (termici o fotovoltaici) a servizio di singoli edifici su coperture piane in posizioni visibili dagli spazi pubblici esterni.', 208);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 76, 'checkbox'   , 'j', 40000, 'B.9. installazione di microgeneratori eolici con altezza complessiva non superiore a ml 1,50 e diametro non superiore a ml 1,00, qualora tali interventi interessino i beni vincolati ai sensi del Codice, art. 136, comma 1, lettera a), b) e c) limitatamente, per quest''ultima, agli immobili di interesse storico-architettonico o storico-testimoniale, ivi compresa l''edilizia rurale tradizionale, isolati o ricompresi nei centri o nuclei storici.', 209);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 77, 'checkbox'   , 'j', 40000, 'B.10. installazione di cabine per impianti tecnologici a rate o colonnine modulari ovvero sostituzione delle medesime con altre diverse per tipologia, dimensioni e localizzazione.', 210);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 78, 'checkbox'   , 'j', 40000, 'B.11. interventi puntuali di adeguamento della viabilità esistente, quali sistemazioni di rotatorie, riconfigurazione di incroci stradali, realizzazione di banchine, pensiline, marciapiedi e percorsi ciclabili, manufatti necessari per la sicurezza della circolazione, realizzazione di parcheggi a raso con fondo drenante o che assicuri adeguata permeabilità del suolo.', 211);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 79, 'checkbox'   , 'j', 40000, 'B.12. interventi sistematici di arredo urbano comportanti l''installazione di manufatti e componenti, compresi gli impianti di pubblica illuminazione.', 212);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 80, 'checkbox'   , 'j', 40000, 'B.13. opere di urbanizzazione primaria previste in piani attuativi già valutati ai fini paesaggistici, ove non siano oggetto di accordi di collaborazione tra il Ministero, le regioni e gli enti locali o di specifica disciplina contenuta nel piano paesaggistico approvato ai sensi dell''art. 143 del Codice.', 213);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 81, 'checkbox'   , 'j', 40000, 'B.14. interventi di cui alla voce A.12 dell''Allegato «A», da eseguirsi nelle aree di pertinenza degli edifici, ove si tratti di beni vincolati ai sensi dell''art. 136, comma 1, lettera b) del Codice.', 214);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 82, 'checkbox'   , 'j', 40000, 'B.15. interventi di demolizione senza ricostruzione di edifici, e manufatti edilizi in genere, privi di interesse architettonico, storico o testimoniale', 215);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 83, 'checkbox'   , 'j', 40000, 'B.16. realizzazione di autorimesse, collocate fuori terra ovvero parzialmente interrate, con volume emergente fuori terra non superiore a 50 mc, compresi i percorsi di accesso e le eventuali rampe', 216);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 84, 'checkbox'   , 'j', 40000, 'B.17. realizzazione di tettoie, porticati, chioschi da giardino di natura permanente e manufatti consimili aperti su più lati, aventi una superficie non superiore a 30 mq o di manufatti accessori o volumi tecnici con volume emergente fuori terra non superiore a 30 mc', 217);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 85, 'checkbox'   , 'j', 40000, 'B.18. interventi sistematici di configurazione delle aree di pertinenza di edifici esistenti, diversi da quelli di cui alla voce B.14, quali: nuove pavimentazioni, accessi pedonali e carrabili, modellazioni del suolo incidenti sulla morfologia del terreno, realizzazione di rampe, opere fisse di arredo, modifiche degli assetti vegetazionali', 218);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 86, 'checkbox'   , 'j', 40000, 'B.19. installazione di tettoie aperte di servizio a capannoni destinati ad attività produttive, o di collegamento tra i capannoni stessi, entro il limite del 10 per cento della superficie coperta preesistente', 219);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 87, 'checkbox'   , 'j', 40000, 'B.20. impianti tecnici esterni al servizio di edifici esistenti a destinazione produttiva, quali strutture per lo stoccaggio dei prodotti ovvero per la canalizzazione dei fluidi o dei fumi mediante tubazioni esterne', 220);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 88, 'checkbox'   , 'j', 40000, 'B.21. realizzazione di cancelli, recinzioni, muri di cinta o di contenimento del terreno, inserimento di elementi antintrusione sui cancelli, le recinzioni e sui muri di cinta, interventi di manutenzione, sostituzione o adeguamento dei medesimi manufatti, se eseguiti con caratteristiche morfotipologiche, materiali o finiture diversi da quelle preesistenti e, comunque, ove interessino beni vincolati ai sensi del Codice, art. 136, comma 1, lettere a), b) e c) limitatamente, per quest''ultima, agli immobili di interesse storico-architettonico o storico-testimoniale, ivi compresa l''edilizia rurale tradizionale, isolati o ricompresi nei centri o nuclei storici', 221);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 89, 'checkbox'   , 'j', 40000, 'B.22. taglio, senza sostituzione, di alberi, ferma l''autorizzazione degli uffici competenti, ove prevista; sostituzione o messa a dimora di alberi e arbusti nelle aree, pubbliche o private, vincolate ai sensi dell''art. 136, comma 1, lettere a) e b) del Codice, ferma l''autorizzazione degli uffici competenti, ove prevista', 222);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 90, 'checkbox'   , 'j', 40000, 'B.23. realizzazione di opere accessorie in soprasuolo correlate alla realizzazione di reti di distribuzione locale di servizi di pubblico interesse o di fognatura, o ad interventi di allaccio alle infrastrutture a rete', 223);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 91, 'checkbox'   , 'j', 40000, 'B.24. posa in opera di manufatti parzialmente o completamente interrati quali serbatoi e cisterne,ove comportanti la modifica permanente della morfologia del terreno o degli assetti vegetazionali, comprese le opere di recinzione o sistemazione correlate; posa in opera in soprasuolo dei medesimi manufatti, con dimensioni non superiori a 15 mc, e relative opere di recinzione o sistemazione', 224);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 92, 'checkbox'   , 'j', 40000, 'B.25. occupazione temporanea di suolo privato, pubblico, o di uso pubblico, mediante installazione di strutture o di manufatti semplicemente ancorati al suolo senza opere murarie o di fondazione per manifestazioni, spettacoli, eventi, o per esposizioni e vendita di merci, per un periodo superiore a 120 e non superiore a 180 giorni nell''anno solare', 225);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 93, 'checkbox'   , 'j', 40000, 'B.26. verande e strutture in genere poste all''esterno (dehors), tali da configurare spazi chiusi funzionali ad attività economiche quali esercizi di somministrazione di alimenti e bevande, attività commerciali, turistico-ricettive, sportive o del tempo libero; installazione di manufatti amovibili o di facile rimozione, consistenti in opere di carattere non stagionale e a servizio della balneazione, quali, ad esempio, chioschi, servizi igienici e cabine; prima collocazione ed installazione dei predetti manufatti amovibili o di facile rimozione aventi carattere stagionale', 226);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 94, 'checkbox'   , 'j', 40000, 'B.27. manufatti in soprasuolo correlati alla realizzazione di pozzi ed opere di presa e prelievo dafalda per uso domestico', 227);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 95, 'checkbox'   , 'j', 40000, 'B.28. realizzazione di ponticelli di attraversamento di corsi d''acqua, o tombinamento parziale dei medesimi, limitatamente al tratto necessario per dare accesso ad edifici esistenti o a fondi agricoli interclusi; riapertura di tratti tombinati di corsi d''acqua', 228);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 96, 'checkbox'   , 'j', 40000, 'B.29. manufatti per ricovero attrezzi agricoli, realizzati con opere murarie o di fondazione, con superficie non superiore a dieci metri quadrati', 229);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 97, 'checkbox'   , 'j', 40000, 'B.30. realizzazione di nuove strutture relative all''esercizio dell''attività ittica con superficie non superiore a 30 mq', 230);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 98, 'checkbox'   , 'j', 40000, 'B.31. interventi di adeguamento della viabilità vicinale e poderale eseguiti nel rispetto della normativa di settore', 231);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES ( 99, 'checkbox'   , 'j', 40000, 'B.32. interventi di ripristino delle attività agricole e pastorali nelle aree rurali invase da formazioni di vegetazione arbustiva o arborea, previo accertamento del preesistente uso agricolo o pastorale da parte delle autorità competenti, ove eseguiti in assenza di piano paesaggistico regionale che individui tali aree', 232);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (100, 'checkbox'   , 'j', 40000, 'B.33. interventi di diradamento boschivo con inserimento di colture agricole di radura', 233);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (101, 'checkbox'   , 'j', 40000, 'B.34. riduzione di superfici boscate in aree di pertinenza di immobili esistenti, per superfici non superiori a 2.000 mq, purché preventivamente assentita dalle amministrazioni competenti', 234);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (102, 'checkbox'   , 'j', 40000, 'B.35. interventi di realizzazione o adeguamento della viabilità forestale in assenza di piani o strumenti di gestione forestale approvati dalla Regione previo parere favorevole del Soprintendente per la parte inerente la realizzazione o adeguamento della viabilità forestale', 235);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (103, 'checkbox'   , 'j', 40000, 'B.36. posa in opera di cartelli e altri mezzi pubblicitari non temporanei di cui all''art. 153, comma 1, del Codice, di dimensioni inferiori a 18 mq, ivi compresi le insegne e i mezzi pubblicitari a messaggio o luminosità variabile, nonché l''installazione di insegne fuori dagli spazi vetrina o da altre collocazioni consimili a ciò preordinate', 236);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (104, 'checkbox'   , 'j', 40000, 'B.37. installazione di linee elettriche e telefoniche su palo a servizio di singole utenze di altezza non superiore, rispettivamente, a metri 10 e a metri 6,30', 237);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (105, 'checkbox'   , 'j', 40000, 'B.38. installazione di impianti delle reti di comunicazione elettronica o di impianti radioelettrici, diversi da quelli di cui all''art. 6, comma 4, del decreto-legge 12 settembre 2014, n. 133, convertito con modificazioni, dalla legge 11 novembre 2014, n. 164, che comportino la realizzazione di supporti di antenne non superiori a 6 metri se collocati su edifici esistenti, e/o la realizzazione di sopralzi di infrastrutture esistenti come pali o tralicci, non superiori a 6 metri, e/o la realizzazione di apparati di telecomunicazioni a servizio delle antenne, costituenti volumi tecnici, tali comunque da non superare l''altezza di metri 3 se collocati su edifici esistenti e di metri 4 se posati direttamente a terra', 238);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (106, 'checkbox'   , 'j', 40000, 'B.39. interventi di modifica di manufatti di difesa dalle acque delle sponde dei corsi d''acqua e dei laghi per adeguamento funzionale', 239);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (107, 'checkbox'   , 'j', 40000, 'B.40. interventi sistematici di ingegneria naturalistica diretti alla regimazione delle acque, alla conservazione del suolo o alla difesa dei versanti da frane e slavine', 240);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (108, 'checkbox'   , 'j', 40000, 'B.41. interventi di demolizione e ricostruzione di edifici e manufatti, ivi compresi gli impianti tecnologici, con volumetria, sagoma ed area di sedime corrispondenti a quelle preesistenti, diversi dagli interventi necessitati di ricostruzione di edifici e manufatti in tutto o in parte crollati o demoliti in conseguenza di calamità naturali o catastrofi. Sono esclusi dal procedimento semplificato gli interventi di demolizione e ricostruzione che interessino i beni di cui all''art. 136, comma 1, lettere a) e b) del Codice', 241);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (109, 'checkbox'   , 'j', 40000, 'B.42. interventi di ripascimento circoscritti di tratti di arenile in erosione, manutenzione di dune artificiali in funzione antierosiva, ripristino di opere di difesa esistenti sulla costa', 242);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (110, 'checkbox'   , 'f', 4    , 'a) lavori, realizzati in assenza o difformità dall''autorizzazione paesaggistica, che non abbiano determinato creazione di superfici utili o volumi ovvero aumento di quelli legittimamente realizzati;', 1);
