INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (128, 'checkbox'   , 'p', 40000, '2. Interventi di demolizione e ricostruzione con il rispetto di volumetria e sagomature preesistenti;', 102);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (129, 'checkbox'   , 'p', 40000, '3. Interventi di demolizione senza ricostruzione o demolizioni di superfetazioni;', 103);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (130, 'checkbox'   , 'p', 40000, '4. Interventi sui prospetti degli edifici esistenti, quali:\n - aperture di porte e finestre o modifica delle aperture esistenti per dimensione e posizione; - interventi sulle finiture esterne, con rifacimento di intonaci, tinteggiature o rivestimenti esterni, - modificativi di quelli preesistenti; - realizzazione o modifica di balconi o terrazze; - inserimento o modifica di cornicioni, ringhiere, parapetti; chiusura di terrazze o di balconi già chiusi su tre lati mediante installazione di infissi; - realizzazione, modifica o sostituzione di scale esterne\n (la presente voce non si applica agli immobili soggetti a tutela ai sensi dell''articolo 136, comma 1, lettere a), b) e c), del Codice);', 104);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (131, 'checkbox'   , 'p', 40000, '5. interventi sulle coperture degli edifici esistenti, quali:\n - rifacimento del manto del tetto e delle lattonerie con materiale diverso; - modifiche indispensabili per l''installazione di impianti tecnologici; - modifiche alla inclinazione o alla configurazione delle falde; - realizzazione di lastrici solari o terrazze a tasca di piccole dimensioni; - inserimento di canne fumarie o comignoli; - realizzazione o modifica di finestre a tetto e lucernari; - realizzazione di abbaini o elementi consimili\n (la presente voce non si applica agli immobili soggetti a tutela ai sensi dell''articolo 136, comma 1, lettere a), b) e c), del Codice);', 105);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (132, 'checkbox'   , 'p', 40000, '6. modifiche che si rendono necessarie per l''adeguamento alla normativa antisismica ovvero per il contenimento dei consumi energetici degli edifici;', 106);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (133, 'checkbox'   , 'p', 40000, '7. realizzazione o modifica di autorimesse pertinenziali, collocate fuori terra ovvero parzialmente o totalmente interrate, con volume non superiore a 50 mc, compresi percorsi di accesso ed eventuali rampe. Ogni successivo intervento di realizzazione o modifica di autorimesse pertinenziale allo stesso immobile è sottoposto a procedura autorizzatoria ordinaria;', 107);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (134, 'checkbox'   , 'p', 40000, '8. realizzazione di tettoie, porticati, chioschi da giardino e manufatti consimili aperti su più lati, aventi una superficie non superiore a 30 mq;', 108);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (135, 'checkbox'   , 'p', 40000, '9. realizzazione di manufatti accessori o volumi tecnici di piccole dimensioni (volume non superiore a 10 mc);', 109);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (136, 'checkbox'   , 'p', 40000, '10. interventi necessari al superamento delle barriere architettoniche, anche comportanti modifica dei prospetti o delle pertinenze esterne degli edifici, ovvero realizzazione o modifica di volumi tecnici. Sono fatte salve le procedure semplificate ai sensi delle leggi speciali di settore(la presente voce non si applica agli immobili soggetti a tutela ai sensi dell''articolo 136, comma 1, lettere a), b) e c), del Codice);', 110);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (137, 'checkbox'   , 'p', 40000, '11. realizzazione o modifica di cancelli, recinzioni, o muri di contenimento del terreno (la presente voce non si applica agli immobili soggetti a tutela ai sensi dell''articolo 136, comma 1, lettere a), b) e c), del Codice);(la presente voce non si applica agli immobili soggetti a tutela ai sensi dell''articolo 136, comma 1, lettere a), b) e c), del Codice);', 111);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (138, 'checkbox'   , 'p', 40000, '12. interventi di modifica di muri di cinta esistenti senza incrementi di altezza;', 112);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (139, 'checkbox'   , 'p', 40000, '13. interventi sistematici nelle aree di pertinenza di edifici esistenti, quali: pavimentazioni, accessi pedonali e carrabili di larghezza non superiore a 4 m, modellazioni del suolo, rampe o arredi fissi (la presente voce non si applica agli immobili soggetti a tutela ai sensi dell''articolo 136, comma 1, lettere a), b) e c), del Codice);', 113);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (140, 'checkbox'   , 'p', 40000, '14. realizzazione di monumenti ed edicole funerarie all''interno delle zone cimiteriali;', 114);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (141, 'checkbox'   , 'p', 40000, '15. posa in opera di cartelli e altri mezzi pubblicitari non temporanei di cui all''art. 153, comma 1 del Codice, di dimensioni inferiori a 18 mq, ivi comprese le insegne per le attività commerciali o pubblici esercizi(la presente voce non si applica agli immobili soggetti a tutela ai sensi dell''articolo 136, comma 1, lettere a), b) e c), del Codice);', 115);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (142, 'checkbox'   , 'p', 40000, '16. collocazione di tende da sole sulle facciate degli edifici per locali destinati ad attività commerciali e pubblici esercizi;', 116);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (143, 'checkbox'   , 'p', 40000, '17. interventi puntuali di adeguamento della viabilità esistente, quali: adeguamento di rotatorie, riconfigurazione di incroci stradali, realizzazione di banchine e marciapiedi, manufatti necessari per la sicurezza della circolazione, nonché quelli relativi alla realizzazione di parcheggi a raso a condizione che assicurino la permeabilità del suolo, sistemazione e arredo di aree verdi;', 117);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (144, 'checkbox'   , 'p', 40000, '18. interventi di allaccio alle infrastrutture a rete, ove comportanti la realizzazione di opere in soprasuolo;', 118);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (145, 'checkbox'   , 'p', 40000, '19. linee elettriche e telefoniche su palo a servizio di singole utenze di altezza non superiore, rispettivamente, a metri 10 e a metri 6,30;', 119);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (146, 'checkbox'   , 'p', 40000, '20. adeguamento di cabine elettriche o del gas, ovvero sostituzione delle medesime con altre di tipologia e dimensioni analoghe;', 120);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (147, 'checkbox'   , 'p', 40000, '21. interventi sistematici di arredo urbano comportanti l''installazione di manufatti e componenti, compresi gli impianti di pubblica illuminazione;', 121);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (148, 'checkbox'   , 'p', 40000, '22. installazione di impianti tecnologici esterni per uso domestico autonomo, quali condizionatori e impianti di climatizzazione dotati di unità esterna, caldaie, parabole, antenne(la presente voce non si applica agli immobili soggetti a tutela ai sensi dell''articolo 136, comma 1, lettere a), b) e c), del Codice);', 122);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (149, 'checkbox'   , 'p', 40000, '23. parabole satellitari condominiali e impianti di condizionamento esterni centralizzati, nonché impianti per l''accesso alle reti di comunicazione elettronica di piccole dimensioni con superficie non superiore ad 1 mq o volume non superiore ad 1 mc(la presente voce non si applica agli immobili soggetti a tutela ai sensi dell''articolo 136, comma 1, lettere a), b) e c), del Codice);', 123);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (150, 'checkbox'   , 'p', 40000, '24. Installazione di impianti di radiocomunicazioni elettroniche mobili, di cui all''articolo 87 del decreto legislativo 1° agosto 2003, n. 259, che comportino la realizzazione di supporti di antenne non superiori a 6 metri se collocati su edifici esistenti, e/o la realizzazione di sopralzi di infrastrutture esistenti come pali o tralicci, non superiori a 6 metri, e/o la realizzazione di apparati di telecomunicazioni a servizio delle antenne, costituenti volumi tecnici, tali comunque da non superare l''altezza di metri 3 se collocati su edifici esistenti e di metri 4 se posati direttamente a terra;', 124);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (151, 'checkbox'   , 'p', 40000, '25. installazione in soprasuolo di serbatoi di GPL di dimensione non superiore a 13 mc, e opere di recinzione e sistemazione correlate;', 125);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (152, 'checkbox'   , 'p', 40000, '26. impianti tecnici esterni al servizio di edifici esistenti a destinazione produttiva, quali sistemi per la canalizzazione dei fluidi mediante tubazioni esterne, lo stoccaggio dei prodotti e canne fumarie;', 126);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (153, 'checkbox'   , 'p', 40000, '27. posa in opera di manufatti completamente interrati (serbatoi, cisterne etc.), che comportino la modifica della morfologia del terreno, comprese opere di recinzione o sistemazione correlate;', 127);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (154, 'checkbox'   , 'p', 40000, '28. pannelli solari, termici e fotovoltaici fino ad una superficie di 25 mq (la presente voce non si applica nelle zone territoriali omogenee \"A\" di cui all''articolo 2 del decreto ministeriale n. 1444 del 1968, e ad esse assimilabili, e nelle aree vincolate ai sensi dell''articolo 136, comma 1, lettere b) e c), del Codice), ferme restando le diverse e più favorevoli previsioni del decreto legislativo 30 maggio 2008, n. 115, recante \"Attuazione della direttiva 2006/32/CE relativa all''efficienza degli usi finali dell''energia e i servizi energetici e abrogazione della direttiva 93/76/CEE\", e dell''articolo 1, comma 289, della legge 24 dicembre 2007, n. 244, recante \"Disposizioni per la formazione del bilancio annuale e pluriennale dello Stato(legge finanziaria 2008)\";', 128);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (155, 'checkbox'   , 'p', 40000, '29. nuovi pozzi, opere di presa e prelievo da falda per uso domestico, preventivamente assentiti dalle Amministrazioni competenti, comportanti la realizzazione di manufatti in soprasuolo;', 129);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (156, 'checkbox'   , 'p', 40000, '30. tombinamento parziale di corsi d''acqua per tratti fino a 4 m ed esclusivamente per dare accesso ad abitazioni esistenti e/o a fondi agricoli interclusi, nonché la riapertura di tratti tombinati di corsi d''acqua;', 130);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (157, 'checkbox'   , 'p', 40000, '31. interventi di ripascimento localizzato di tratti di arenile in erosione, manutenzione di dune artificiali in funzione antierosiva, ripristino di opere di difesa esistenti sulla costa;', 131);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (158, 'checkbox'   , 'p', 40000, '32. ripristino e adeguamento funzionale di manufatti di difesa dalle acque delle sponde dei corsi d''acqua e dei laghi;', 132);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (159, 'checkbox'   , 'p', 40000, '33. taglio selettivo di vegetazione ripariale presente sulle sponde o sulle isole fluviali;', 133);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (160, 'checkbox'   , 'p', 40000, '34. riduzione di superfici boscate in aree di pertinenza di immobili esistenti, per superfici non superiori a 100 mq, preventivamente assentita dalle amministrazioni competenti;', 134);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (161, 'checkbox'   , 'p', 40000, '35. ripristino di prati stabili, prati pascolo, coltivazioni agrarie tipiche, mediante riduzione di aree boscate di recente formazione per superfici non superiori a 5000 mq, preventivamente assentiti dalle amministrazioni competenti;', 135);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (162, 'checkbox'   , 'p', 40000, '36. taglio di alberi isolati o in gruppi, ove ricompresi nelle aree di cui all''articolo 136, comma 1, lettere c) e d), del Codice, preventivamente assentito dalle amministrazioni competenti;', 136);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (163, 'checkbox'   , 'p', 40000, '37. manufatti realizzati in legno per ricovero attrezzi agricoli, con superficie non superiore a 10 mq;', 137);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (164, 'checkbox'   , 'p', 40000, '38. occupazione temporanea di suolo privato, pubblico, o di uso pubblico, con strutture mobili, chioschi e simili, per un periodo superiore a 120 giorni;', 138);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (165, 'checkbox'   , 'p', 40000, '39. strutture stagionali non permanenti collegate ad attività turistiche, sportive o del tempo libero, da considerare come attrezzature amovibili.', 139);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (166, 'checkbox'   , 'a', 1, 'piani urbanistici esecutivi (L.R. 20/2001);', 1);
INSERT INTO tipi_e_qualificazioni (id, stile, raggruppamento, zona, label, ordine) VALUES (167, 'checkbox'   , 'a', 1, 'piani Particolareggiati di Esecuzione, (L. 1150/1942 ess. mm. e ii);', 2);
